SELECT ino,title,poster,subject,jiyoek,price,gubun,suggest,num FROM (SELECT ino,title,poster,subject,jiyoek,price,gubun,suggest,rownum as num FROM (SELECT ino,title,poster,subject,jiyoek,price,gubun,suggest FROM inte_detail
WHERE ${column} LIKE '%'||#{fd}||'%'
ORDER BY ino ASC)) WHERE num BETWEEN #{start} AND #{end}
