mapper.select:
SELECT id,email,name,nickname,sex,birthday,phone,addr1,addr2,post,admin,num FROM(SELECT id,email,name,nickname,sex,birthday,phone,addr1,addr2,post,admin,rownum as num FROM (SELECT id,email,name,nickname,sex,birthday,phone,addr1,addr2,post,admin FROM amem
WHERE ${col} LIKE '%'||#{fd}||'%'
ORDER BY id ASC )) WHERE num BETWEEN #{start} AND #{end}

 SELECT no,etype,dprice,name,type,addr,state,num 
 FROM (SELECT no,etype,dprice,name,type,addr,state,rownum as num
 FROM (SELECT no,etype,dprice,name,type,addr,state
 FROM estate ESTATE_DETAIL WHERE company=#{name}

 AND ${col} LIKE '%'||#{fd}||'%' 

 ORDER BY no ASC
))
WHERE  num BETWEEN #{start} AND #{end}

SELECT CEIL(COUNT(*)/10.0)
FROM ESTATE_DETAIL WHERE company=#{name}

  AND ${col} LIKE '%'||#{fd}||'%'
